package com.example.android.politicalpreparedness.electionimport android.os.Bundleimport androidx.fragment.app.Fragmentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.navigation.fragment.navArgsimport com.example.android.politicalpreparedness.databinding.FragmentMyCustomWebviewBindingclass MyCustomWebViewFragment : Fragment() {    val args : MyCustomWebViewFragmentArgs by navArgs()    override fun onCreateView(inflater: LayoutInflater, container: ViewGroup?,                              savedInstanceState: Bundle?): View? {      val binding = FragmentMyCustomWebviewBinding.inflate(inflater)        binding.webview.loadUrl(args.url)      return binding.root    }}